# | string                            | first start-of-message marker is complete after |
# |-----------------------------------|------------------------------------------------|
# | mjqjpqmgbljsphdztnvjfqwrcgsmlb    | 19                                             |
# | bvwbjplbgvbhsrlpgdmjqwftvncz      | 23                                             |
# | nppdvjthqldpwncqszvftbrmjlhg      | 12                                             |
# | nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg | 29                                             |
# | zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw  | 26                                             |

# How many characters need to be processed before the first start-of-packet marker is detected?
# start-of-packet marker = occurence of 14 different chars



def AllCharsDifferent():
    for char in stream:
        amount = stream.count(char)
        if amount > 1:
            return False
        
    return True


# puzzle input
input = ''

#input = 'zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw'

stream = []
position = 0
for char in input:
    if len(stream) < 14:
        stream.append(char)
        position = position + 1
    else:
        if AllCharsDifferent() == True:
            print(stream, position)
            break
        else:
            del stream[0]
            stream.append(char)
            position = position + 1
